import React from 'react';
import { connect } from 'react-redux';
import Modal from '../Modal';
import history from '../../history';
import { fetchStream, deleteStream }  from '../../actions';

const StreamDelete = () => {
    const goBack = () => history.push('/')
    return (
        <div>
            Delete Stream
            <Modal onOutsideClick={goBack}>
                <div className="header">Delete Stream</div>
                <div className="content">Are you sure you wannt to delete this stream?</div>
                <div className="actions">
                    <button className="ui button" onClick={goBack}>Cancel</button>
                    <button className="ui button negative">Delete</button>
                </div>
            </Modal>
        </div>
    );
}

const mapStateToProps = (state, ownProps) => {
    //turn stream object back to array, because it's easier to map over the array
    return {
        stream: state.streams[ownProps.match.params.id]
    }
}

export default connect(null, {mapStateToProps})(StreamDelete);
